    static let textWordList: [String] = {
        return split(text.characters){$0 == " "}.map(String.init)
        }()
    
    class func generate(words: Int) -> String {
        var wordCount = words
        
        if wordCount > textWordList.count {
            wordCount = textWordList.count
        }
        
        return " ".join(Array(textWordList[0..<wordCount]))
